import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(() => {
    axios.get('http://backend-service.todo-app.svc.cluster.local:5000/api/tasks')
      .then((res) => setTasks(res.data))
      .catch((err) => console.error('Error fetching tasks:', err));
  }, []);

  const addTask = () => {
    axios.post('http://backend-service.todo-app.svc.cluster.local:5000/api/tasks', { text: newTask })
      .then((res) => {
        setTasks([...tasks, res.data]);
        setNewTask('');
      });
  };

  const deleteTask = (id) => {
    axios.delete(`http://backend-service.todo-app.svc.cluster.local:5000/api/tasks/${id}`)
      .then(() => setTasks(tasks.filter((task) => task._id !== id)));
  };

  return (
    <div className="App">
      <h1>To-Do List</h1>
      <input value={newTask} onChange={(e) => setNewTask(e.target.value)} placeholder="Add a task" />
      <button onClick={addTask}>Add</button>
      <ul>
        {tasks.map((task) => (
          <li key={task._id}>
            {task.text} <button onClick={() => deleteTask(task._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
